def.body:
if status == Status::CLOSED
  return
end


@status = Status::DRAINING_SUBS
@subs.each_value(&.unsubscribe)
@subs.clear


@socket_mutex.synchronize do
  @pongs.each(&.close)
  @pongs.clear

  if @socket.closed?
  else
    @status = Status::DRAINING_PUBS
    @socket.flush
    @socket.close
  end
  @status = Status::CLOSED
end

@prefix = String::Builder.build(PREFIX_LENGTH) do |io|
  Random::Secure.random_bytes(@pre_bytes)
  @pre_bytes.each do |n|
    io << DIGITS[n % BASE]
  end
end
